"use client";
import { useEffect, useState } from "react";
import { useParams, useRouter } from "next/navigation";
import { Trip } from "@/types/trip";

const DEFAULT_IMAGE = "/default-trip.jpg";

export default function TripDetailPage() {
  const { id } = useParams<{ id: string }>();
  const router = useRouter();

  const [trip, setTrip] = useState<Trip | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadTrip = async () => {
      try {
        const res = await fetch(`/api/trips/${id}`);

        if (!res.ok) {
          alert("Trip not found");
          router.push("/trips");
          return;
        }

        const data = await res.json();
        setTrip(data);
      } catch {
        alert("Failed to load trip");
        router.push("/trips");
      } finally {
        setLoading(false);
      }
    };

    loadTrip();
  }, [id, router]);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        Loading trip...
      </div>
    );
  }

  if (!trip) return null;

  return (
    <section className="min-h-screen bg-gray-50 dark:bg-gray-950 px-4 py-24">
      <div className="max-w-4xl mx-auto">

        {/* Cover Image */}
        <div className="rounded-xl overflow-hidden shadow-lg mb-8">
          <img
            src={trip.image || DEFAULT_IMAGE}
            alt={trip.title}
            className="w-full h-[420px] object-cover"
          />
        </div>

        {/* Content */}
        <div className="bg-white dark:bg-gray-900 rounded-xl p-6 border border-gray-200 dark:border-gray-700">
          <h1 className="text-3xl md:text-4xl font-semibold text-gray-900 dark:text-gray-100 mb-3">
            {trip.title}
          </h1>

          <p className="text-gray-600 dark:text-gray-400 mb-2">
            ≡ƒôì {trip.location}, {trip.city}
          </p>

          <p className="text-sm text-gray-500 mb-6">
            ≡ƒùô {trip.date}
          </p>

          <p className="text-gray-800 dark:text-gray-300 leading-relaxed">
            {trip.description}
          </p>

          {/* Back button */}
          <button
            onClick={() => router.back()}
            className="mt-8 inline-flex items-center px-5 py-2 rounded-md bg-blue-600 hover:bg-blue-700 text-white transition"
          >
            ΓåÉ Back to Trips
          </button>
        </div>
      </div>
    </section>
  );
}

"use client";
import { useEffect, useState } from "react";
import { useRouter } from "next/navigation";
import TripCard from "@/components/TripCard";
import EditTripForm from "@/components/EditTripForm";
import Link from "next/link";
import { Trip } from "@/types/trip";
import { User } from "@/types/user";

export default function Trips() {
  const router = useRouter();

  const [trips, setTrips] = useState<Trip[]>([]);
  const [editingTrip, setEditingTrip] = useState<Trip | null>(null);
  const [currentUser, setCurrentUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(true);

  const openTrip = (id: number) => {
    router.push(`/trips/${id}`);
  };

  // ≡ƒö╣ Load trips + current user
  const loadData = async () => {
    try {
      const [tripRes, userRes] = await Promise.all([
        fetch("/api/trips"),
        fetch("/api/auth/me"),
      ]);

      if (tripRes.ok) {
        const tripData = await tripRes.json();
        setTrips(tripData);
      }

      if (userRes.ok) {
        const userData = await userRes.json();
        setCurrentUser(userData.user);
      } else {
        setCurrentUser(null);
      }
    } catch (err) {
      console.error("Failed to load data");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadData();
  }, []);

  // ≡ƒö╣ Delete trip
  const handleDelete = async (id: number) => {
    const isConfirmed = window.confirm(
      "Are you sure you want to delete this trip?"
    );
    if (!isConfirmed) return;

    const res = await fetch(`/api/trips/${id}`, {
      method: "DELETE",
    });

    if (!res.ok) {
      alert("Not authorized or failed to delete");
      return;
    }

    setTrips((prev) => prev.filter((trip) => trip.id !== id));
  };

  // ≡ƒö╣ Save edited trip
  const handleSaveEdit = async (updatedTrip: Trip) => {
    const res = await fetch(`/api/trips/${updatedTrip.id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(updatedTrip),
    });

    if (!res.ok) {
      alert("Failed to update trip");
      return;
    }

    const savedTrip = await res.json();

    setTrips((prev) =>
      prev.map((trip) =>
        trip.id === savedTrip.id ? savedTrip : trip
      )
    );

    setEditingTrip(null);
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        Loading trips...
      </div>
    );
  }

  return (
    <section className="min-h-screen bg-gradient-to-b from-gray-50 to-white dark:from-gray-900 dark:to-gray-950 px-4 py-24">
      <div className="container mx-auto max-w-7xl">

        {/* Header */}
        <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-10 gap-4">
          <div>
            <h2 className="text-3xl md:text-4xl bg-gradient-to-r from-gray-900 to-gray-700 dark:from-gray-100 dark:to-gray-300 bg-clip-text text-transparent">
              Trips
            </h2>
            <p className="text-gray-600 dark:text-gray-400 mt-1">
              Manage and explore all your planned adventures
            </p>
          </div>

          <Link
            href="/add-trip"
            className="inline-flex items-center justify-center rounded-xl bg-gradient-to-r from-blue-600 to-teal-600 px-6 py-3 text-white font-medium hover:opacity-90 transition shadow-lg shadow-blue-500/30"
          >
            + Add Trip
          </Link>
        </div>

        {/* Trips Grid */}
        {trips.length === 0 ? (
          <div className="text-center py-24">
            <p className="text-gray-600 dark:text-gray-400 mb-6">
              No trips added yet. Start planning your first adventure!
            </p>
            <Link
              href="/add-trip"
              className="rounded-xl bg-gradient-to-r from-blue-600 to-teal-600 px-6 py-3 text-white font-medium hover:opacity-90 transition shadow-lg shadow-blue-500/30"
            >
              Add Your First Trip
            </Link>
          </div>
        ) : (
          <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-8">
            {trips.map((trip) => (
              <div
                key={trip.id}
                className="bg-white dark:bg-gray-800 rounded-3xl shadow-xl hover:shadow-2xl transition-shadow p-1"
              >
                <TripCard
                  trip={trip}
                  canEdit={currentUser?.id === trip.userId}
                  onDelete={handleDelete}
                  onEdit={setEditingTrip}
                  onOpen={openTrip}
                />
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Edit Modal */}
      {editingTrip && (
        <div className="fixed inset-0 z-50 flex items-center justify-center px-4">
          <div
            className="absolute inset-0 bg-black/50 backdrop-blur-sm"
            onClick={() => setEditingTrip(null)}
          />

          <div
            className="relative z-10 w-full max-w-md bg-white dark:bg-gray-800 rounded-3xl shadow-2xl p-8"
            onClick={(e) => e.stopPropagation()}
          >
            <EditTripForm
              trip={editingTrip}
              onSave={handleSaveEdit}
              onCancel={() => setEditingTrip(null)}
            />
          </div>
        </div>
      )}
    </section>
  );
}

