"use client";
import { useEffect, useState } from "react";
import { Moon, Sun } from "lucide-react";

export default function DarkModeToggle() {
  const [dark, setDark] = useState(false);

  // ≡ƒö╣ Load theme from cookie or system
  useEffect(() => {
    const cookieTheme = document.cookie
      .split("; ")
      .find((row) => row.startsWith("theme="))
      ?.split("=")[1];

    const prefersDark = window.matchMedia(
      "(prefers-color-scheme: dark)"
    ).matches;

    const isDark = cookieTheme
      ? cookieTheme === "dark"
      : prefersDark;

    document.documentElement.classList.toggle("dark", isDark);
    setDark(isDark);
  }, []);

  const toggleDarkMode = () => {
    const newTheme = !dark;
    document.documentElement.classList.toggle("dark", newTheme);
    document.cookie = `theme=${newTheme ? "dark" : "light"}; path=/; max-age=31536000`;
    setDark(newTheme);
  };

  return (
    <button
      onClick={toggleDarkMode}
      aria-label="Toggle Dark Mode"
      className="
        w-10 h-10 rounded-full
        border border-gray-200 dark:border-gray-700
        flex items-center justify-center
        bg-transparent
        hover:bg-gray-100 dark:hover:bg-gray-800
        transition-colors
      "
    >
      {dark ? (
        <Sun size={18} className="text-yellow-400" />
      ) : (
        <Moon size={18} className="text-gray-700 dark:text-gray-200" />
      )}
    </button>
  );
}

"use client";
import { useState } from "react";
import { Trip } from "@/types/trip";

type Props = {
  trip: Trip;
  onSave: (trip: Trip) => void;
  onCancel: () => void;
};

export default function EditTripForm({ trip, onSave, onCancel }: Props) {
  const [title, setTitle] = useState(trip.title);
  const [location, setLocation] = useState(trip.location);
  const [city, setCity] = useState(trip.city);
  const [date, setDate] = useState(trip.date);
  const [description, setDescription] = useState(trip.description);
  const [image, setImage] = useState(trip.image || "");
  const [saving, setSaving] = useState(false);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!title || !location || !city || !date || !description) {
      alert("All fields except image are required");
      return;
    }

    setSaving(true);

    onSave({
      ...trip,
      title,
      location,
      city,
      date,
      description,
      image: image || undefined,
    });
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="w-full max-w-md mx-auto bg-white dark:bg-gray-900 rounded-lg border border-gray-200 dark:border-gray-700 p-6 space-y-4"
    >
      <h2 className="text-xl font-semibold text-gray-900 dark:text-gray-100">
        Edit Trip
      </h2>

      <input
        className="input"
        placeholder="Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        required
      />

      <input
        className="input"
        placeholder="Location"
        value={location}
        onChange={(e) => setLocation(e.target.value)}
        required
      />

      <input
        className="input"
        placeholder="City"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        required
      />

      <input
        type="date"
        className="input"
        value={date}
        onChange={(e) => setDate(e.target.value)}
        required
      />

      <textarea
        className="input resize-none h-28"
        placeholder="Trip description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        required
      />

      <input
        className="input"
        placeholder="Image URL (optional)"
        value={image}
        onChange={(e) => setImage(e.target.value)}
      />

      {image && (
        <img
          src={image}
          alt="Preview"
          className="w-full h-40 object-cover rounded-md border"
        />
      )}

      <div className="flex justify-end gap-3 pt-2">
        <button
          type="button"
          onClick={onCancel}
          className="px-4 py-2 rounded-md border border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-800 transition"
        >
          Cancel
        </button>

        <button
          type="submit"
          disabled={saving}
          className="px-4 py-2 rounded-md bg-blue-600 hover:bg-blue-700 text-white transition disabled:opacity-60"
        >
          {saving ? "Saving..." : "Save Changes"}
        </button>
      </div>
    </form>
  );
}

